import React, { Fragment } from 'react';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import { useSelector } from 'react-redux';


const AllProducts = () => {
    const product = useSelector((state) => state.product);
    let card = [];
    const handleAdd = (product) => {
        let product1 = [product.id, product.title, product.price, product.imageUrl, 1];
        let flag = false;
        let pros = '';
        if(localStorage.productscard){
            pros = JSON.parse(localStorage.productscard);
            for (let index = 0; index < pros.length; index++) {
                let element = pros[index];

                if(element[0] == product1[0]){
                    // alert('qablan ezafe shode');
                    // return false;
                    element[4] += 1; 
                    flag = true;
                }   
            }
        }
        if(flag){
            card = pros;
            localStorage.setItem('productscard', JSON.stringify(pros));
        } else{
            card.push(product1);
            localStorage.setItem('productscard', JSON.stringify(card));
        }
       
         };

    const [getIsActive, setIsActive] = useState(false);
    const handleActiveSecondPageOnClick = () => {
        setIsActive(!getIsActive);
    };
    const handleActiveFirstPageOnClick = () => {
        setIsActive(!getIsActive);
    };
    return (
        <Fragment>
            <div className='row all-products-section'>
                <div className='col-md-2 text-right p-3'>
                   
                        <div className='all-products-category'>
                            <h2>دسته بندی ها</h2>
                            <div className='all-products-category-list'>
                                <ul>
                                    <li className='p-2 list-unstyled'>
                                        <a href='#'>آرایشی</a>
                                    </li>
                                    <li className='p-2 list-unstyled'>
                                        <a href='#'>پوست</a>
                                    </li>
                                    <li className='p-2 list-unstyled'>
                                        <a href='#'>مو</a>
                                    </li>
                                    <li className='p-2 list-unstyled'>
                                        <a href='#'>بدن</a>
                                    </li>
                                </ul>
                            </div>
                        </div>
                  
                </div>
                <div className='col-md-10 text-right'>
               
                        <div className='row all-products-sort-order'>
                            <select>
                                <option> مرتب سازی </option>
                                <option> قیمت </option>
                                <option> مدرت زمان دوره </option>
                                <option> تاریخ انتشار </option>
                            </select>
                        </div>
                        <div className='row products last-products'>
                            {product.map((product) => (
                                <div className='col-lg-3 col-md-4 text-right py-3' key={product.id}>
                                    
                                    <div className='inner-product-item'>
                                            <Link to='/product'>
                                                <div className='product-img'>
                                                    <img
                                                        src={product.imageUrl}
                                                        alt=''
                                                    />
                                                </div>
                                                <div className='product-data'>
                                                    <h2>{product.title}</h2>
                                                    <div className='price'>
                                                        <span>{product.price}</span>
                                                        <span>تومان</span>
                                                    </div>
                                                </div>
                                            </Link>
                                            <div className='add-to-card'>
                               
                                                    <button  onClick={() => { handleAdd(product) }} > 
                                                        <i className='fa fa-shopping-cart'></i>
                                                        <span
                                                            className='add-to-card-span'
                                                            id='span1'
                                                        >
                                                            افزودن به سبد خرید
                                                        </span>
                                                    </button>
                                               
                                            </div>
                                        </div>
                                </div>

                            ))}


                        </div>
                        <div className='pagination-container'>
                            <ul className='pagination justify-content-center'>
                                <li
                                    className={`page-item
                                ${getIsActive ? 'null' : 'disabled'} `}
                                    onClick={handleActiveFirstPageOnClick}
                                >
                                    <a className='page-link' href='#'>
                                        صفحه قبلی
                                    </a>
                                </li>
                                <li
                                    className={`page-item
                                ${getIsActive ? 'null' : 'active'} `}
                                    onClick={handleActiveFirstPageOnClick}
                                >
                                    <a className='page-link' href='#'>
                                        1
                                    </a>
                                </li>
                                <li
                                    className={`page-item
                                ${getIsActive ? 'active' : 'null'} `}
                                    onClick={handleActiveSecondPageOnClick}
                                >
                                    <a className='page-link ' href='#'>
                                        2
                                    </a>
                                </li>
                                <li
                                    className={`page-item
                                ${getIsActive ? 'disabled' : 'null'} `}
                                >
                                    <a
                                        className='page-link'
                                        href='#'
                                        onClick={handleActiveSecondPageOnClick}
                                    >
                                        صفحه بعدی
                                    </a>
                                </li>
                            </ul>
                        </div>
              
                </div>
            </div>
        </Fragment>
    );
};

export default AllProducts;
