import React, { Fragment, useState } from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector } from 'react-redux';

const Cart = () => {
    var products = useSelector((state) => state.product);
    const [getCount, setCount] = useState();
    const [getprolen, setprolen] = useState(products.length);

    console.log(products);
    let card = [];
    var arr = [];
    var prolen = getprolen;
    const handleAdd = (product) => {
        setCount(1);
        let product1 = [
            product.id,
            product.title,
            product.description,
            product.imageUrl,
            1,
        ];
        let flag = false;
        let pros = '';
        if (localStorage.productscard) {
            pros = JSON.parse(localStorage.productscard);

            for (let index = 0; index < pros.length; index++) {
                let element = pros[index];

                if (element[0] == product1[0]) {
                    element[4] += 1;
                    flag = true;
                    setCount(element[4]);
                }
            }
        }
        if (flag) {
            card = pros;
            localStorage.setItem('productscard', JSON.stringify(pros));
        } else {
            card.push(product1);
            localStorage.setItem('productscard', JSON.stringify(card));
        }
    };

    const change = (e, product, i) => {
        if (e.target.value < 1) {
            e.target.value = '';
            // setCount("");
            document.getElementsByClassName('price2')[i].innerHTML =
                product.price;
        } else {
            e.value = e.target.value;
            // setCount(e.target.value);
            var price3 = product.price * e.target.value;
            document.getElementsByClassName('price2')[
                i
            ].innerHTML = price3
                .toString()
                .replace(/\B(?=(\d{3})+(?!\d))/g, ',');
        }
    };

    const uperCount = (e, product, i) => {
        let input_counter = e.target.parentNode.parentNode.getElementsByClassName(
            'input-counter'
        )[0];
        if (input_counter.value <= 1) input_counter.value = 1;
        input_counter.value = parseInt(input_counter.value) + 1;
        var x = document.getElementsByClassName('price2')[i].innerText;
        var y = product.price;
        var price3 = parseInt(y) * input_counter.value;

        document.getElementsByClassName('price2')[
            i
        ].innerHTML = price3.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
    };

    const downerCount = (e, product, i) => {
        let input_counter = e.target.parentNode.parentNode.getElementsByClassName(
            'input-counter'
        )[0];
        if (input_counter.value <= 1) input_counter.value = 1;
        else input_counter.value = parseInt(input_counter.value) - 1;
        var x = document.getElementsByClassName('price2')[i].innerText;
        var y = product.price;
        var price3 = parseInt(y) * input_counter.value;

        document.getElementsByClassName('price2')[
            i
        ].innerHTML = price3.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
    };

    const slideUp = (e, i) => {
        console.log(getprolen);
        setprolen(getprolen - 1);
        console.log(getprolen);
        var mdiv = document.getElementsByClassName('card-products-item')[i];
        mdiv.style.transition = 'all .5s ease-in-out';
        mdiv.style.height = '0px';

        mdiv.style.borderColor = 'transparent';
        document.getElementsByClassName(
            'card-products-item'
        )[0].lastElementChild.style.borderBottom = 'none';
        if (i > 0) {
            document.getElementsByClassName('card-products-item')[
                i - 1
            ].style.borderBottom = 'none';
            document.getElementsByClassName('card-products-item')[
                i - 1
            ].style.borderColor = 'transparent';
        }

        if (getprolen === 0) {
            document.getElementsByClassName('card-products')[0].style.border =
                'none';
            console.log('nnn');
        }
    };

    return (
        <Fragment>
          
                <div className='container '>
                    <div className='row '>
                        <div className='col-md-12 py-5  '>
                            <div className=' py-2  mb-5 heading text-right'>
                          
                                <h1 className='  text-right '>سبد خرید</h1>
                            </div>
                            <div className='row card-products w-100 p-0 m-0 mb-5'>
                                {products.map((product, i) => (
                                    <div
                                        className='col-md-12 card-products-item p-0'
                                        key={i}
                                    >
                                        <div className='row w-100 p-4 m-0'>
                                            <div className='col-md-3 p-0'>
                                                <img
                                                    className='card-item-img'
                                                    src='./img/iphon.png'
                                                    alt=''
                                                />
                                            </div>
                                            <div className='col-md-9 p-0 d-flex flex-column justify-content-between'>
                                                <div className='align-left'>
                                                    <button
                                                        className='card-product-trash'
                                                        onClick={(e) => {
                                                            slideUp(e, i);
                                                        }}
                                                    >
                                                        <img
                                                            className='card-item-icon-trash1 '
                                                            src='./img/red-trash.png'
                                                            alt=''
                                                        />
                                                        <img
                                                            className='card-item-icon-trash2 '
                                                            src='./img/white-trash.png'
                                                            alt=''
                                                        />
                                                    </button>
                                                </div>

                                                <h2 className='text-right mb-3'>
                                                    گوشی iphon 11pro max
                                                </h2>
                                                <div className='text-right mb-2'>
                                                    دو سیم کارنه با حافظه 568
                                                    گیگابایت
                                                </div>
                                                <div className='text-right mb-1'>
                                                    <i class='fa fa-circle ml-1'></i>
                                                    <span>مشکی</span>
                                                </div>
                                                <div className='text-right  mb-1'>
                                                    <img
                                                        className='card-item-icon ml-1'
                                                        src='./img/card-guarantee.png'
                                                        alt=''
                                                    />
                                                    <span>گارانتی 6 ماهه</span>
                                                </div>
                                                <div className='text-right  mb-1'>
                                                    <img
                                                        className='card-item-icon ml-1'
                                                        src='./img/card-available.png'
                                                        alt=''
